#!/bin/bash

cat << EOF > tmp_inversion_ref.fa
>control

EOF

cat << EOF > tmp_inversion_que.fq
>read1

+

EOF


minimap2 -ax sr tmp_inversion_ref.fa tmp_inversion_que.fq --cs=long > inversion_sr_simulated.sam

minimap2 -ax splice tmp_inversion_ref.fa tmp_inversion_que.fq --cs=long > inversion_splice_simulated.sam

rm tmp_inversion*
